var notifications = [];

const NOTIFICATION_TYPES = {
    Pushy: 'App\\Notifications\\PushDemo'
};

$(document).ready(function() {
  
    // check if there's a logged in user
    if(Laravel.userId) {
       
        $.get('notifications', function (data) {
            addNotifications(data, "#notifications");
        });
    }
});

function addNotifications(newNotifications, target) {
    notifications = _.concat(notifications, newNotifications);
    // show only last 5 notifications
    notifications.slice(0, 5);
    showNotifications(notifications, target);
}
function showNotifications(notifications, target) {
    if(notifications.length) {
        var htmlElements = notifications.map(function (notification) {
            return makeNotification(notification);
        });
        $(target + 'Menu').html(htmlElements.join(''));
        $(target).addClass('has-notifications')
    } else {
        $(target + 'Menu').html('<li class="dropdown-header">Geen Meldingen</li>');
        $(target).removeClass('has-notifications');
    }
}
function makeNotification(notification) {
    var to = routeNotification(notification);
    var notificationText = makeNotificationText(notification);
    return '<li><a href="' + to + '">' + notificationText + '</a></li>';
}

// get the notification route based on it's type
function routeNotification(notification) {
    var to = '?read=' + notification.id;
    if(notification.type === NOTIFICATION_TYPES.Pushy) {
        to = 'users' + to;
    }
    return '/' + to;
}

// get the notification text based on it's type
function makeNotificationText(notification) {
    var text = '';
    if(notification.type === NOTIFICATION_TYPES.Pushy) {
        const title = notifications.data.Database_Title;
        const message = notification.data.Database_Message;
        text += '<strong>' + title + '</strong><br>' + message;
    }
    return text;
}